import React from 'react';
import styled from 'styled-components';
import { ProductConsumer } from '../context';

const SideCart = () => {
	return (
		<ProductConsumer>
			{value => {
				console.log(value);
				const { cartOpen, closeCart } = value;
				return <CartWrapper show={cartOpen} onClick={closeCart}></CartWrapper>;
			}}
		</ProductConsumer>
	);
};

export default SideCart;

const CartWrapper = styled.div`
	position: fixed;
	top: 70px;
	right: 0;
	color: black;
	height: 100%;
	background: white;
	z-index: 1;
	font-size: 3rem;
	width: 50%;
	transform: ${props => (props.show ? 'translateX(0)' : 'translate(-100vw)')};
`;
