import React from 'react';
import { ProductConsumer } from '../context';
import heroPic from '../images/pexels-sound-on-3756853.jpg';
import styled from 'styled-components';
import watch from '../ssss.png';
import airpod from '../airpod.png';
import iphone from '../iphone-.png';

export default function Home() {
	return (
		<>
			<ProductConsumer>
				{value => {
					return (
						<HomeWrapper>
							<div className='banner'>
								<img src={heroPic} alt='' />
								<h2>Cool Gadgets and Techy stuff</h2>
							</div>
							<div className='banner2'>
								<div className='images'>
									<img src={watch} alt='' className='a' />
									<img src={airpod} alt='' className='b' />
									<img src={iphone} alt='' className='c' />
								</div>
							</div>
						</HomeWrapper>
					);
				}}
			</ProductConsumer>
		</>
	);
}

const HomeWrapper = styled.section`
	width: 100%;
	.banner {
		width: 100%;
		height: 300px;
	}
	.banner img {
		width: 100%;
		height: 250px;
	}
	.banner h2 {
		width: calc(100% - 2rem);
		margin: 1rem 1rem;
		font-size: 1.5rem;
		padding: 1rem;
		font-family: 'Bungee';
		color: orange;
		text-align: center;
		border: 3px solid #f2a3bd;
		text-shadow: 0px 2px 4px rgba(0, 0, 0, 0.1);
	}
	.banner2 {
		width: calc(100% - 2rem);
		// background: #e0218a;
		margin: 3rem 1rem;
	}
	.banner2 .images {
		padding: 1rem;
		display: grid;
		grid-template-columns: repeat(2, 45%);
		grid-template-rows: repeat(2, 120px);
		grid-column-gap: 10%;
		grid-row-gap: 10px;
		justify-content: center;
		align-items: center;
	}
	.banner2 img {
		background: #0000aa;
		height: 100%;
		width: 100%;
		box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.3), 0px -4px 4px rgba(0, 0, 0, 0.3);
	}
	.b {
		grid-row: 1/3;
	}
`;
