import React from 'react';
import styled from 'styled-components';
import banner from '../images/productsbg.jpg';
import provec from '../images/provec.jpg';
import { ProductConsumer } from '../context';
import Filter from '../components/Filter';

const Products = () => {
	return (
		<ProductConsumer>
			{value => {
				const { filteredProducts } = value;

				return (
					<ProductsWrapper>
						<div className='banner'>
							<img src={banner} alt='' />
						</div>
						<div className='pro-container'>
							<div className='products-title'>
								<h3>Products</h3>
							</div>
						</div>
						<Filter />
						{/* products */}

						{filteredProducts.map(product => {
							return (
								<div className='products'>
									<div className='product-card' key={product.id}>
										<img src={product.images[0].fields.file.url} alt='' />
										{product.name}
										<br />
										{product.price}
										<button className='btn-details'>Details</button>
									</div>
								</div>
							);
						})}
					</ProductsWrapper>
				);
			}}
		</ProductConsumer>
	);
};

export default Products;

const ProductsWrapper = styled.div`
	position: relative;
	top: 70px;
	background: url(${provec});
	background-position: center;
	background-attachment: fixed;
	background-size: cover;
	.banner {
		width: 100%;
		height: 250px;
	}
	.banner img {
		width: 100%;
		height: 100%;
	}
	.pro-container {
		background: white;
		margin: 0;
		padding: 2rem;
	}
	.products-title {
		font-family: 'Poppins';
		margin: 0 auto;
		text-align: center;
		width: 150px;
	}
	h3 {
		position: relative;
		font-weight: 800;
		margin: 0;
	}
	.products {
		margin-top: 25vh;
		padding-top: 4rem;
		background: #fff;
		display: grid;
		width: 100%;
		grid-template-columns: repeat(1, 270px);
		grid-template-rows: repeat(auto-fit, 200px);
		justify-content: center;
		text-align: center;
		grid-row-gap: 2rem;
	}
	.product-card {
		display: grid;
		grid-template-columns: repeat(1, 100%);
		grid-template-rows: repeat(3, 90px);
		text-align:center;
		padding-top: 5rem;
		box-shadow: 0px 4px 4px rgba(0,0,0,0.4), 0px -4px 4px rgba(0,0,0,0.4);

	}

	.product-card img {
		height: 90px;
        width: 50px
		grid-row: 1/2;
		margin: 0 auto;
	}

	.btn-details{
		padding: 1rem;
	}
`;
