import React from 'react';
import styled from 'styled-components';
import { ProductConsumer } from '../context';
import CartRow from '../components/CartRow';
import Cart from '../images/cart.jpg';

const CartPage = () => {
	return (
		<CartPageWrapper>
			<ProductConsumer>
				{value => {
					const { cart } = value;
					return (
						<>
							<div className='banner'>
								<img src={Cart} alt='' />
							</div>
							<p className='banner-txt'>Your Cart</p>
							<div className='line'></div>
							<div className='cart-grid'>
								<div className='row-head'>
									<div className='col-1'>Products</div>
									<div className='col-2'>Name</div>
									<div className='col-3'>Price</div>
									<div className='col-4'>Quantity</div>
									<div className='col-5'>Remove</div>
									<div className='col-6'>Total</div>
								</div>

								{cart.map(item => {
									return (
										<div className='cart-row' key={item.id}>
											<CartRow item={item} className={item.id / 2 ? 2 : '1'} />
										</div>
									);
								})}
							</div>
						</>
					);
				}}
			</ProductConsumer>
		</CartPageWrapper>
	);
};

export default CartPage;
const CartPageWrapper = styled.section`
	position: relative;
	top: 70px;
	width: 100%;
	.banner {
        width 100%;

    }

    .banner img{
        width: 100%;
    }

    .banner-txt{
        font-size: 2rem;    
        padding: 1rem 2rem;
        text-align:center;
        font-family: 'Poppins';
        margin: 0;
        font-weight: bold;
    }

    .line{
        margin: 0 auto;
        width: 70px;
        height: 8px;
        background: orange;
    }
    .2{
        background:red;
    }

`;
