import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { FaSearch, FaCartPlus } from 'react-icons/fa';
import { ProductConsumer } from '../context';
import { useSpring, animated } from 'react-spring';

const ProductCard = props => {
	const { product } = props;

	const { images, name, price, id } = product;
	const eff = useSpring({
		vector: [0, 10, 30],
		display: 'block',
		padding: 20,
		background: 'linear-gradient(to right, #009fff, #ec2f4b)',
		transform: 'translate3d(0px,0,0) scale(1) rotateX(0deg)',
		boxShadow: '0px 10px 20px 0px rgba(0,0,0,0.4)',
		borderBottom: '10px solid #2D3747',
		shape: 'M20,20 L20,380 L380,380 L380,20 L20,20 Z',
		textShadow: '0px 5px 15px rgba(255,255,255,0.5)',
	});

	return (
		<animated.ProductWrapper className={product.id === 1 ? 'full' : ''}>
			<img src={images[0].fields.file.url} alt={name} />
			<p>{name}</p>

			<p className='price-tag'>{price}$</p>
			<ProductConsumer>
				{value => {
					return (
						<div className='icons'>
							<Link
								to={`/products/${product.name}`}
								onClick={() => {
									value.setSingleProduct(id);
									value.getSingleProduct(id);
								}}>
								<FaSearch className='a' />
							</Link>
							<FaCartPlus className='b' />
						</div>
					);
				}}
			</ProductConsumer>
		</animated.ProductWrapper>
	);
};

export default ProductCard;
const ProductWrapper = styled.article`
	width: 100%;
	height: 100%;
	position: relative;
	background: white;
	transition: all 0.3s linear;
	box-shadow: 5px 3px 3px rgba(0, 0, 0, 0.3), 0px -3px 3px rgba(0, 0, 0, 0.3);

	img {
		width: 100%;
	}
	p {
		margin: 0.5rem;
		font-size: 2rem;
		color: orange;
	}
	.icons {
		position: absolute;
		top: 50%;
		width: 100%;
		display: none;
	}
	.a,
	.b {
		transform: scale(2);
		margin: 0 2rem;
		cursor: pointer;
	}

	.price-tag {
		margin: 1rem auto;
		background: orange;
		color: #fff;
		border-radius: 50%;
		width: 70px;
	}
	:hover {
		z-index: 99;
		transform: scale(1.09);
		img {
			opacity: 0.5;
		}

		.glow {
			display: block;
		}

		.icons {
			display: block;
		}
	}

	// ipad
	@media screen and (min-width: 500px) {
		.icons {
			position: absolute;
			top: 70%;
			width: 100%;
			display: none;
		}
	}
	// desktop
	@media screen and (min-width: 1024px) {
		.icons {
			position: absolute;
			top: 50%;
			width: 100%;
			display: none;
		}
	}
`;
