import styled from 'styled-components';
import React, { useRef, useEffect } from 'react';
import { ProductConsumer } from '../context';
import Rellax from 'rellax';

const Product = props => {
	const rellaxRef = useRef();
	useEffect(() => {
		new Rellax(rellaxRef.current);
	}, []);

	return (
		<ProductConsumer>
			{value => {
				const { singleProduct } = value;
				const mainImg = singleProduct.images[0].fields.file.url;
				return (
					<ProductWrapper
						ref={rellaxRef}
						data-rellax-speed='-9'
						style={{
							background: `url(${mainImg})`,
							backgroundSize: 'cover',
							backgroundPosition: 'center',
						}}>
						<div
							style={{
								fontSize: '2rem',
								color: 'red',
								background: 'white',
								position: 'absolute',
							}}
							ref={rellaxRef}
							data-rellax-speed='7'>
							Lorem, ipsum dolor sit amet consectetur adipisicing elit. Cum
							molestias eos ipsum temporibus ipsa quisquam vitae consequuntur
							nulla harum quod laboriosam reiciendis minus, excepturi nam quos,
							fugit quibusdam numquam nihil odit amet unde! Veritatis
							accusantium nam eos tempora consequuntur laboriosam perferendis
							eveniet! Expedita provident laborum nihil, labore dolore alias
							voluptatum magnam! Maiores, nostrum soluta nobis voluptates enim
							molestias beatae quos esse accusamus earum blanditiis pariatur vel
							similique laudantium magnam repudiandae veniam dolores debitis
							alias sapiente natus, perferendis harum aspernatur reprehenderit.
						</div>
					</ProductWrapper>
				);
			}}
		</ProductConsumer>
	);
};

export default Product;
const ProductWrapper = styled.section`
	position: relative;
	top: 70px;
	width: 100%;
	height: 100%;
	background-size: cover;
	div {
		margin: 10rem 3rem;
	}
`;
