import styled from 'styled-components';
import React, { useEffect } from 'react';
import { ProductConsumer } from '../context';
import productbg from '../images/productbg.jpg';
import _ from 'lodash';

const Product = props => {
	useEffect(() => {
		// window.scrollTo(0, 0);
	}, []);

	return (
		<ProductConsumer>
			{value => {
				const { singleProduct } = value;
				const { features } = singleProduct;

				const mainImg = singleProduct.images[0].fields.file.url;
				const featuresArray = Object.keys(features).map(k => ({
					[k]: features[k],
				}));
				console.log(singleProduct);
				const featuresList = featuresArray.map(feature => {
					return (
						<li key={_.uniqueId('Key_')}>
							{JSON.stringify(feature).slice(1, -1).replace(/"/g, '')}
						</li>
					);
				});

				return (
					<ProductWrapper
						style={{
							background: `url(${productbg})`,
							backgroundSize: 'cover',
							backgroundPosition: 'center',
							backgroundRepeat: 'no-repeat',
							backgroundAttachment: 'fixed',
						}}>
						<div className='img-container'>
							<img src={mainImg} alt='' />
						</div>
						<div className='properties'>
							<div className='prop-img-container'>
								<img src={singleProduct.images[1].fields.file.url} alt='' />
							</div>
							<div className='features'>
								<p>SPECS</p>
								<h3>
									{singleProduct.name} <span className='line'></span>{' '}
								</h3>

								<ul>{featuresList}</ul>
							</div>
						</div>
					</ProductWrapper>
				);
			}}
		</ProductConsumer>
	);
};

export default Product;
const ProductWrapper = styled.section`
	position: relative;
	top: 70px;
	width: 100%;
	height: 100%;

	.img-container {
		width: 100%;
		height: 100vh;
		text-align: center;
		background: rgba(255, 255, 255, 0.6);
	}
	.img-container img {
		margin-top: 100px;
		width: 300px;
		height: 250px;
	}
	.properties {
		width: 100%;
		display: grid;
		grid-template-columns: repeat(1, 1fr);
		grid-row-gap: 2rem;
		background: #fff;
		align-items: center;
		justify-content: center;
		padding-top: 3rem;
	}
	.prop-img-container {
		width: 100%;
		height: 100%;
	}
	.prop-img-container img {
		width: 100%;
	}
	.features {
		padding: 1rem 0;
		display: grid;
		grid-template-columns: repeat(2, 50%);
		grid-template-rows: repeat(auto-fit, 90px));
	}

	.features h3 {
		text-align: center;
		grid-column: 1/3;
	}
	.features p{
		grid-row: 2/3;
		transform: rotate(90deg);
	}
	.line {
		width: calc(100% / 4);
		height: 8px;
		background: hotpink;
		margin: 0 auto;
	}
	.features ul {
		list-style: none;
		color: black;
		font-family: 'Poppins';
		font-size: 1.6rem;
		margin: 0;
		padding: 0;
	}
	.features ul li {
		margin: 1rem;
		padding: 5px;
	}
`;
