import styled from 'styled-components';
import React, { useRef, useEffect } from 'react';
import { ProductConsumer } from '../context';
import Rellax from 'rellax';

const Product = props => {
	const { getProductInfo } = props;
	console.log(props);
	const rellaxRef = useRef();
	useEffect(() => {
		new Rellax(rellaxRef.current, {
			speed: -10,
			center: false,
			wrapper: null,
			round: true,
			vertical: true,
			horizontal: false,
		});
	}, []);

	return (
		<ProductConsumer>
			{value => {
				value.featuredProducts.filter(item => {
					item.name === props.match.params.slug;
					return item;
				});

				return (
					<ProductWrapper>
						<div ref={rellaxRef} className='a'>
							My first layer
						</div>
						<div>My second layer</div>
					</ProductWrapper>
				);
			}}
		</ProductConsumer>
	);
};

export default Product;
const ProductWrapper = styled.section`
	position: relative;
	top: 70px;
	width: 100%;
	height: 900px;
	background: ;
`;
