import React from 'react';
import styled from 'styled-components';
import { FaHamburger, FaCartPlus } from 'react-icons/fa';
import { motion } from 'framer-motion';

export default function Navbar() {
	const iconVariant = {
		visible: {
			opacity: 0.1,
		},
		hover: {
			opacity: 1,
			x: [0, 20, -20, 20, -20, 0],
		},
	};
	return (
		<NavWrapper>
			<motion.div
				variants={{ iconVariant }}
				whileHover='hover'
				animate='visible'>
				<FaHamburger className='nav-icon' />
			</motion.div>
			<div className='nav-bran'>TechBuddy</div>
			<motion.div
				variants={{ iconVariant }}
				whileHover='hover'
				animate='visible'>
				<FaCartPlus className='nav-icon' />
			</motion.div>
		</NavWrapper>
	);
}

const NavWrapper = styled.nav`
	width: 100%;
	margin: 0;
	background: transparent;
	padding: 2rem;
	color: #fff;
	font-size: 3rem;
	font-family: 'Poppins';
	display: flex;
	align-items: center;
	justify-content: center;
	.nav-bran {
		margin: 0 auto;
		text-shadow: 0px 4px 4px rgba(0, 0, 0, 1);
	}
	.nav-icon {
		margin: 0 4rem;
		font-size: 3rem;
		cursor: pointer;
	}
`;
