import React, { useState } from 'react';
import styled from 'styled-components';
import { FaHamburger, FaCartPlus } from 'react-icons/fa';
import { motion } from 'framer-motion';
import AOS from 'aos';
import 'aos/dist/aos.css';

export default function Navbar() {
	useState(() => {
		AOS.init({
			duration: 2000,
		});
	});
	const iconVariant = {
		visible: {
			opacity: 0.8,
		},
		hover: {
			opacity: 1,
			x: [0, 5, -5, 5, -5, 0],
			textShadow: '0px 0px 8px rgb(255, 255, 255) ',
			transition: {
				delay: 0.2,
			},
		},
	};
	return (
		<NavWrapper data-aos='fade-down'>
			<motion.div variants={iconVariant} whileHover='hover' animate='visible'>
				<FaHamburger className='nav-icon' />
			</motion.div>
			<div className='nav-bran'>TechBuddy</div>
			<motion.div variants={iconVariant} whileHover='hover' animate='visible'>
				<FaCartPlus className='nav-icon' />
			</motion.div>
		</NavWrapper>
	);
}

const NavWrapper = styled.nav`
	width: 100%;
	position: absolute;
	top: 0;
	left: 0;
	margin: 0;
	padding: 0 !important;
	background: transparent;
	padding: 2rem !important;
	color: #fff;
	font-size: 3rem;
	font-family: 'Poppins';
	display: flex;
	align-items: center;
	justify-content: center;
	.nav-bran {
		margin: 0 auto;
		text-shadow: 0px 4px 4px rgba(0, 0, 0, 1);
	}
	.nav-icon {
		margin: 0 4rem;
		font-size: 3rem;
		cursor: pointer;
	}
`;
