import React from 'react';
import { ProductConsumer } from '../context';
import heroPic from '../pexels-garrett-morrow-682933.jpg';
import styled from 'styled-components';
import watch from '../watch2.png';
import airpod from '../airpod.png';

export default function Home() {
	return (
		<>
			<ProductConsumer>
				{value => {
					return (
						<HomeWrapper>
							<div className='banner'>
								<h2>Cool Gadgets and Techy stuff</h2>
							</div>
							<div className='banner2'>
								<div className='images'>
									<img src={watch} alt='' />
									<img src={airpod} alt='' />
								</div>
							</div>
						</HomeWrapper>
					);
				}}
			</ProductConsumer>
		</>
	);
}

const HomeWrapper = styled.section`
	height: 100vh;
	background: url(${heroPic});
	background-size: cover;
	background-position: center;
	overflow: hidden;

	.container {
		position: relative;
		background: rgba(0, 0, 0, 0.3);
		width: 100%;
		height: 100%;
		overflow: hidden;
	}
	.banner {
		padding: 2rem;
		color: orange;
		background: black;
		text-align: center;
		display: flex;
		align-items: center;
		justify-content: center;
		transform: skew(0, -30deg);
		position: absolute;
		bottom: -150px;
		right: -160px;
		height: 600px;
		width: 800px;
	}
	.banner h2 {
		font-family: 'Bungee';
		font-size: 6rem;
		margin: 12rem;
	}
	.banner2 {
		position: absolute;
		bottom: 0;
		height: 600px;
		width: 800px;
		transform: skew(0, 30deg);
		background: white;
		bottom: -150px;
		left: -160px;
		padding: 4rem;
		display: flex;
		align-item: center;
		justify-content: center;
	}
	.images {
		height: 200px;
		transform: skew(0, -30deg);
	}
	img {
		height: 100%;
	}
`;
