import React from 'react';
import styled from 'styled-components';
import banner from '../images/productsbg.jpg';
import provec from '../images/provec.jpg';
import { ProductConsumer } from '../context';

const Products = () => {
	return (
		<ProductsWrapper>
			<div className='banner'>
				<img src={banner} alt='' />
			</div>
			<div className='products-title'>
				<h3>Products</h3>
			</div>

			{/* products */}
			{filteredProducts.map(product => {
				return (
					<div key={product.id}>
						<img src={product.images[0].fields.file.url} alt='' width='50px' />
						{product.name}
						<br />
						{product.price}
					</div>
				);
			})}
		</ProductsWrapper>
	);
};

export default Products;

const ProductsWrapper = styled.div`
	position: relative;
	top: 70px;

	.banner {
		width: 100%;
		height: 250px;
	}
	.banner img {
		width: 100%;
		height: 100%;
	}
	.products-title {
		font-family: 'Poppins';
		margin: 2rem auto;
		text-align: center;

		width: 150px;
	}
	h3 {
		position: relative;
		font-weight: 800;
	}
`;
