import React from 'react';
import styled from 'styled-components';
import cart from '../images/cart.jpg';
import { ProductConsumer } from '../context';

const CartPage = () => {
	return (
		<ProductConsumer>
			{value => {
				<CartPageWrapper>
					<div className='banner'>
						<img src={cart} alt='' />
					</div>
					<p className='banner-txt'>Your Cart</p>
					<div className='line'></div>
					<div className='cart-grid'>
						<div className='row-head'>
							<div className='col-1'>Products</div>
							<div className='col-2'>Name</div>
							<div className='col-3'>Price</div>
							<div className='col-4'>Quantity</div>
							<div className='col-5'>Remove</div>
							<div className='col-6'>Total</div>
						</div>
						{}
					</div>
				</CartPageWrapper>;
			}}
		</ProductConsumer>
	);
};

export default CartPage;
const CartPageWrapper = styled.section`
	position: relative;
	top: 71px;
	width: 100%;
	.banner {
        width 100%;

    }

    .banner img{
        width: 100%;
    }

    .banner-txt{
        font-size: 2rem;    
        padding: 1rem 2rem;
        text-align:center;
        font-family: 'Poppins';
        margin: 0;
        font-weight: bold;
    }

    .line{
        margin: 0 auto;
        width: 70px;
        height: 8px;
        background: orange;
    }

    .cart-grid{
        display:grid;
        margin: 2rem 0;
        grid-template-columns: repeat(1, 1fr);
        grid-template-rows: repeat(auto-fit, 90px);
    }
    .row-head{
        display:grid;
        grid-template-columns: repeat(5, 55px);
        grid-column-gap: 9px;
        width: 100%;
        justify-content:Center;
        text-align:center;

    }
    .col-1{
        display:none;
    }
`;
