	}
	.img-container img {
		margin-top: 160px;
		width: 100%;
		height: 250px;
	}
	@media screen and (min-width: 450px) {
		.img-container img {
			height: 350px;
		}
		.b {
			top: 430px;
		}
	}
	.properties {
		width: 100%;
		display: grid;
		grid-template-columns: repeat(1, 1fr);
		grid-row-gap: 2rem;
		background: #fff;
		align-items: center;
		justify-content: center;
		padding-top: 3rem;
	}
	.prop-img-container {
