		.img-container {
			height: 100vh;
		}
		.img-container img {
			height: auto;
		}
		.b {
			top: 700px;

			font-size: 3rem;
		}
		.box {
			top: -50px;
			height: 50px;
			width: 150px;
			border: 3px solid white;
			border-right: none;
			border-bottom: none;
		}
		.txt {
			font-size: 3rem;
			right: -150px;
		}
		.two {
			top: 70px;
			height: 50px;
			width: 150px;
			border: 3px solid white;
			border-top: none;
			border-right: none;
		}
