import styled from 'styled-components';
import React, { useRef, useEffect } from 'react';
import Rellax from 'rellax';

const Product = () => {
	const rellaxRef = useRef();
	useEffect(() => {
		new Rellax(rellaxRef.current, {
			speed: -10,
			center: false,
			wrapper: null,
			round: true,
			vertical: true,
			horizontal: false,
		});
	}, []);

	return (
		<ProductWrapper>
			<div ref={rellaxRef} className='a'>
				My first layer
			</div>
			<div>My second layer</div>
		</ProductWrapper>
	);
};

export default Product;
const ProductWrapper = styled.section`
	position: relative;
	top: 70px;
	width: 100%;
	height: 900px;
	.a {
		position: absolute;
		top: 99%;
	}
`;
