import styled from 'styled-components';
import React, { useEffect } from 'react';
import { ProductConsumer } from '../context';
import productbg from '../images/productbg.jpg';
import _ from 'lodash';

const Product = props => {
	useEffect(() => {
		window.scrollTo(0, 0);
	}, []);

	return (
		<ProductConsumer>
			{value => {
				const { singleProduct } = value;
				const { features } = singleProduct;

				const mainImg = singleProduct.images[0].fields.file.url;
				const featuresArray = _.values(features);
				console.log(featuresArray);
				// getting indexes as keys
				var keys = [];
				featuresArray.forEach((el, i) => (el ? keys.push(i) : null));

				const id = _.uniqueId('Key_');
				console.log(id);
				const featuresList = featuresArray.map(feature => {
					return <li>{feature}</li>;
				});

				return (
					<ProductWrapper
						style={{
							background: `url(${productbg})`,
							backgroundSize: 'cover',
							backgroundPosition: 'center',
							backgroundRepeat: 'no-repeat',
							backgroundAttachment: 'fixed',
						}}>
						<div className='img-container'>
							<img src={mainImg} alt='' />
						</div>
						<div className='properties'>
							<div className='prop-img-container'>
								<img src={singleProduct.images[1].fields.file.url} alt='' />
							</div>
							<div className='features'>
								<ul>{featuresList}</ul>
							</div>
						</div>
					</ProductWrapper>
				);
			}}
		</ProductConsumer>
	);
};

export default Product;
const ProductWrapper = styled.section`
	position: relative;
	top: 70px;
	width: 100%;
	height: 100%;

	.img-container {
		width: 100%;
		height: 100vh;
		text-align: center;
		background: rgba(255, 255, 255, 0.4);
	}
	.img-container img {
		margin-top: 100px;
		width: 300px;
		height: 250px;
	}
	.properties {
		width: 100%;
		height: 500px;
		display: flex;
		flex-direction: column;
		background: #fff;
	}
	.prop-img-container {
		width: 100%;
		height: 100%;
	}
	.prop-img-container img {
		width: 100%;
	}
`;
