import React from 'react';
import styled from 'styled-components';
import banner from '../images/productsbg.jpg';
import provec from '../images/provec.jpg';
import { ProductConsumer } from '../context';
import Filter from '../components/Filter';

const Products = () => {
	return (
		<ProductConsumer>
			{value => {
				const { filteredProducts } = value;

				return (
					<ProductsWrapper>
						<div className='banner'>
							<img src={banner} alt='' />
						</div>
						<div className='products-title'>
							<h3>Products</h3>
						</div>
						<Filter />
						{/* products */}
						{filteredProducts.map(product => {
							return (
								<div key={product.id}>
									<img
										src={product.images[0].fields.file.url}
										alt=''
										width='50px'
									/>
									{product.name}
									<br />
									{product.price}
								</div>
							);
						})}
					</ProductsWrapper>
				);
			}}
		</ProductConsumer>
	);
};

export default Products;

const ProductsWrapper = styled.div`
	position: relative;
    top: 70px;

	.banner {
		width: 100%;
		height: 250px;
	}
	.banner img {
		width: 100%;
		height: 100%;
	}
	.products-title {
		font-family: 'Poppins';
		margin: 2rem auto;
		text-align: center;

		width: 150px;
	}
	h3 {
		position: relative;
		font-weight: 800;
	}

	.restpart {
		background: url(${provec});
		background-position: center;
		background-attachment: fixed;
		background-size: cover;
		width: 100%;
		color: #fff;
        text-align: center;
        padding-top: 50vh;
    }
    .filter{

        background:#fff;
    }
	label {
        margin: 4rem 0;
        margin-bottom: 0;
		font-size: 2rem;
		font-family: 'Poppins';
        color: black;
        padding: 1rem;
        width:calc(100% - 2rem);

	}
	input, select {
        height: 40px;
        width:calc(100% - 2rem);
		border: 1px solid #ececec;
        margin: 1rem auto;
        font-weight:bold;
        outline:none;
        border-radius:0;

`;
